const pagesKey = 'pages';

let pageJson = localStorage.getItem(pagesKey);
let pagesStore = JSON.parse(pagesJson);

export default {
    getAllPages(){
        return pagesStore;
    },

    getSinglePage(index){
        return pageStore[index];
    }
}
