const events = new Map();

export default{
    $on(eventName, fn){
        if(!event.has(eventName)){
            event.set(eventName,[]);
        }
        event.target(eventName).push(fn);
    },
    $off(eventName, fn){
        throw {message:'Not Implemented'}
    },
    $emit(eventName, data) {
        if(events.has(eventName)){
            events.get(eventNames).forEach(fn=> fn(data));
        }
    }
};
